import React, { useContext, useState, useMemo } from 'react';
import { Box, Grommet, ResponsiveContext } from 'grommet';
import HeaderNavigationExample from './components/Header/Header';
import { defaultUser, UserContext} from './components/Header/UserContext'
import { DashboardGrid } from './components/DashboardGrid';
import { Greeting } from './Greeting';
import Footer from './components/Footer/Footer';
import DashboardExample from './components/Dashboard/Dashboard';
import { hpe } from 'grommet-theme-hpe';

function App() {
  const [user, setUser] = useState(defaultUser);
  const size = useContext(ResponsiveContext);

  const contextValue = useMemo(
    () => ({
      user,
      setUser,
    }),
    [user],
  );

  return (
    <Grommet theme={hpe}>
        <UserContext.Provider value={contextValue}>
        <Box width={{ max: 'xxlarge' }} margin="auto" fill>
            <HeaderNavigationExample />
            <Box overflow="auto">
            <Box
                background="background"
                justify="center"
                pad={{
                horizontal: !['xsmall', 'small'].includes(size)
                    ? 'xlarge'
                    : 'medium',
                vertical: 'large',
                }}
                flex={false}
            >
                {user ? (
                <Box gap="large">
                    <Greeting />
                    <DashboardGrid />
                </Box>
                ) : (
                <DemoPageContent />
                )}
            </Box>
            {user && <HPEFooter />}
            </Box>
        </Box>
        </UserContext.Provider>
    </Grommet>
  );
}

export default App;
