// SignIn.js
import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import {
  Box,
  Button,
  Form,
  FormField,
  Grommet,
  Header,
  Heading,
  ResponsiveContext,
  Text,
  TextInput,
} from 'grommet';
import { Close, Next, CircleAlert } from 'grommet-icons';
import { emailValidation } from '../components/Form/FormValidation';
import { hpe } from 'grommet-theme-hpe';
import { getAuth, setPersistence, signInWithEmailAndPassword, browserSessionPersistence } from "firebase/auth";
import { Link, useNavigate } from "react-router-dom";

const ResetPassword = ({ closeLayer, email }) => {
  const [formValues, setFormValues] = React.useState({ resetEmail: email });

    // eslint-disable-next-line no-unused-vars
    const onSubmit = ({ value, touched }) => {
      // Your password reset logic here
      // Display success status
      closeLayer();
    };

  return (
    <>
      <Box
        direction="row"
        justify="end"
        pad={{ horizontal: 'small', top: 'small' }}
      >
        <Button
          a11yTitle="Close reset password form"
          icon={<Close />}
          onClick={closeLayer}
        />
      </Box>
      <Box
        gap="medium"
        margin={{ horizontal: 'xlarge', bottom: 'xlarge', top: 'large' }}
        width="medium"
      >
        <Heading level={2} margin="none">
          Reset Password
        </Heading>
        <Form
          validate="blur"
          value={formValues}
          onChange={setFormValues}
          onSubmit={({ value, touched }) => onSubmit({ value, touched })}
          method="post"
        >
          <Box gap="medium">
            <Text>
              An email to reset your password will be sent to the following
              address:
            </Text>
            <FormField
              label="Email"
              htmlFor="resetEmail"
              name="resetEmail"
              validate={emailValidation}
            >
              <TextInput
                id="resetEmail"
                name="resetEmail"
                type="email"
                placeholder="your.email@company.com"
              />
            </FormField>
            <Button label="Send password reset" primary type="submit" />
          </Box>
        </Form>
      </Box>
    </>
  );
};

export const SignInExample = () => {
  const [formValues, setFormValues] = React.useState({
    email: '',
    password: '',
  });
  const size = useContext(ResponsiveContext);
  const navigate = useNavigate();
  // eslint-disable-next-line no-unused-vars
  const [credentialError, setCredentialError] = React.useState(false);


  // eslint-disable-next-line no-unused-vars
  const onSubmit = ({ value, touched }) => {
    const auth = getAuth();
    setPersistence(auth, browserSessionPersistence)
    .then(() => {
      // Existing and future Auth states are now persisted in the current
      // session only. Closing the window would clear any existing state even
      // if a user forgets to sign out.
      // ...
      // New sign-in will be persisted with session persistence.
      signInWithEmailAndPassword(auth, email, password)

    })
      .then((userCredential) => {
        // Signed in 
        const user = userCredential.user;
        navigate("/hpe-softcat-leaderboard/")
      })
      .catch((error) => {
        // eslint-disable-next-line
        const errorCode = error.code;
        // eslint-disable-next-line
        const errorMessage = error.message;
        setCredentialError(true);
      });
  };

  return (
    <Grommet theme={hpe}>
      <Box gap="medium" width="medium">
        <Header
            direction="column"
            align="start"
            gap="xxsmall"
            pad={{ horizontal: 'xxsmall' }}
        >
            {/* Use semantically correct heading level and adjust size as 
            needed. In this instance, this example is presented within an 
            HTML section element and this is the first heading within the 
            section, therefor h2 is the semantically correct heading. For 
            additional detail, see https://design-system.hpe.design/foundation/typography#semantic-usage-of-heading-levels). */}
            <Heading level={2} margin="none">
            Sign In
            </Heading>
            <Text>to Hewlett Packard Enterprise</Text>
        </Header>
        <Box
            // Padding used to prevent focus from being cutoff
            pad={{ horizontal: 'xxsmall' }}
        >
          <Form
            validate="blur"
            value={formValues}
            onChange={setFormValues}
            messages={{
                required: 'This is a required field.',
            }}
            onSubmit={({ value, touched }) => onSubmit({ value, touched })}
          >
            <FormField
                label="Email"
                name="email"
                htmlFor="email-sign-in"
                validate={emailValidation}
                required={{ indicator: false }}
            >
                <TextInput
                id="email-sign-in"
                name="email"
                placeholder="james@hpe.com"
                type="email"
                />
            </FormField>
            <FormField
                label="Password"
                htmlFor="password-sign-in"
                name="password"
                required={{ indicator: false }}
            >
                <TextInput
                id="password-sign-in"
                name="password"
                placeholder="Enter your password"
                type="password"
                />
            </FormField>
            {credentialError && (
                <Box
                animation="fadeIn"
                align="center"
                background="validation-critical"
                direction="row"
                gap="xsmall"
                margin={{ top: 'medium', bottom: 'medium' }}
                pad="small"
                round="4px"
                >
                <CircleAlert size="small" />
                <Text size="xsmall">Invalid credentials.</Text>
                </Box>
            )}
            <Box
                align={!['xsmall', 'small'].includes(size) ? 'start' : undefined}
                margin={{ top: 'medium', bottom: 'small' }}
            >
                <Button
                label="Sign In"
                icon={<Next />}
                reverse
                primary
                type="submit"
                />
            </Box>
          </Form>
          <p>Don't have an account? <Link to="/hpe-softcat-leaderboard/SignUp">Register</Link> now.</p>
        </Box>
      </Box>
    </Grommet>
  );
};

ResetPassword.propTypes = {
  closeLayer: PropTypes.func,
  email: PropTypes.string,
};

export default SignInExample
