// your implementation.
// import { demoStyle } from './demoStyle';

const defaultCategories = {
  Trending: { title: 'Trending', sources: [] },
  Classification: { title: 'Classification', sources: [] },
  Population: { title: 'Population', sources: [] },
  Economics: { title: 'Economics', sources: [] },
};

